Click Here For Raw Source<pre><code>&lt;source&gt;
  @type forward
  port 24224
  #tag dockerdaemon.*
  bind 0.0.0.0
&lt;/source&gt;

&lt;filter dockerdaemon.**&gt;
  @type record_transformer
  &lt;record&gt;
    tag ${tag}
  &lt;/record&gt;
&lt;/filter&gt;


&lt;filter dockerdaemon.caddy&gt;
  @type parser
  format json
  reserve_data true
  key_name  log
&lt;/filter&gt;


&lt;filter syslog.**&gt;
  @type record_transformer
  &lt;record&gt;
    hostname "#{Socket.gethostname}"
    tag ${tag}
  &lt;/record&gt;
&lt;/filter&gt;


&lt;match {caddy.access,syslog.**,dockerdaemon.**}&gt;
  @type elasticsearch
  suppress_type_name true
  host "10.8.0.30"
  scheme http
  path ""
  port 32367
  include_tag_key true
  reload_connections false
  reconnect_on_error true
  reload_on_failure false
  logstash_format true
  logstash_prefix "vps9"
  &lt;buffer&gt;
    @type file
    path /var/log/td-agent/buffer
    flush_thread_count 8
    flush_interval 5s
    chunk_limit_size 2M
    queue_limit_length 32
    retry_max_interval 30
    retry_forever true
  &lt;/buffer&gt;
&lt;/match&gt;
</code></pre>
